import React , {useState,useEffect} from 'react'

function About() {
    const [text, setText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const words = ['Mern Stack Developer', 'I can Develop cool websites'];

  useEffect(() => {
    const timeout = setTimeout(handleTyping, 100);
    return () => clearTimeout(timeout);
  }, [currentIndex]);

  const handleTyping = () => {
    const currentWord = words[currentIndex];
    const currentText = text;

    if (!isDeleting) {
      setText(currentWord.substring(0, currentText.length + 1));

      if (currentText === currentWord) {
        setIsDeleting(true);
        setTimeout(() => setCurrentIndex((prevIndex) => (prevIndex + 1) % words.length), 2000);
      }
    } else {
      setText(currentWord.substring(0, currentText.length - 1));

      if (currentText === '') {
        setIsDeleting(false);
      }
    }
  };
  return (
<div className="flex items-center justify-center">
