import React, { useState, useEffect } from 'react';

function About() {
  const [text, setText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const words = ['Mern Stack Developer', 'I can Develop cool websites'];

  useEffect(() => {
    const timeout = setTimeout(handleTyping, 200);
    return () => clearTimeout(timeout);
  }, [text]);

  const handleTyping = () => {
    const currentWord = words[currentIndex];
    const currentText = text;

    if (!isDeleting) {
      setText(currentWord.substring(0, currentText.length + 1));

      if (currentText === currentWord) {
        setIsDeleting(true);
        setTimeout(() => setIsDeleting(false), 2000);
      }
    } else {
      setText(currentText.substring(0, currentText.length - 1));

      if (currentText === '') {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % words.length);
      }
    }
  };

  return (
    <div className="flex items-center justify-center">
      <h1 className="text-3xl">Iâ€™m Mohamed Grissa</h1>
      <h1>{text}</h1>
    </div>
  );
}

export default About;
